import React from 'react';
import { useParams, Link } from 'react-router-dom'; // Import useParams and Link
import Card from '../components/ui/card';
import AnimatedBackground from '../components/ui/AnimatedBackground';

const courseContent = {  // Example course content - replace with your actual data
  Mathematics: {
    title: 'Mathematics Fundamentals',
    topics: [
      { name: 'Algebra', content: 'Content for Algebra goes here...' },
      { name: 'Calculus', content: 'Content for Calculus goes here...' },
      { name: 'Geometry', content: 'Content for Geometry goes here...' },
      // ... more topics
    ],
  },
  Physics: {
    title: 'Introduction to Physics',
    topics: [
      { name: 'Mechanics', content: 'Content for Mechanics goes here...' },
      { name: 'Thermodynamics', content: 'Content for Thermodynamics goes here...' },
      // ... more topics
    ],
  },
  // ... content for other courses
};

const CoursePage = () => {
  const { courseName } = useParams(); // Get courseName from URL
  const course = courseContent[courseName];

  if (!course) {
    return <div>Course not found!</div>; // Handle cases where the course doesn't exist
  }

  return (
    <div className="p-8 bg-gradient-to-br from-blue-50 to-purple-50 min-h-screen relative flex"> {/* Added flex for sidebar */}
      <AnimatedBackground />
      <div className="relative z-10 w-3/4"> {/* Content area (adjust width as needed) */}
        <h1 className="text-4xl font-bold mb-8 text-gray-800">{course.title}</h1>
        {course.topics.map((topic, index) => (
          <Card key={index} className="p-6 bg-white/90 backdrop-blur-sm mb-4">
            <h2 className="text-2xl font-bold text-gray-800 mb-2">{topic.name}</h2>
            <p className="text-gray-600">{topic.content}</p>
          </Card>
        ))}
      </div>
      <aside className="relative z-10 w-1/4 bg-white/90 backdrop-blur-sm p-4 rounded-lg ml-4"> {/* Sidebar */}
        <h2 className="text-xl font-bold mb-4 text-gray-800">Topics</h2>
        <ul>
          {course.topics.map((topic, index) => (
            <li key={index} className="mb-2">
              <Link to={`/courses/${courseName}#${topic.name}`} className="text-blue-600 hover:underline"> {/* Link to topic */}
                {topic.name}
              </Link>
            </li>
          ))}
        </ul>
      </aside>
    </div>
  );
};

export default CoursePage;
