import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Link, Navigate, useNavigate } from 'react-router-dom';
import { CircularProgressbar, buildStyles } from 'react-circular-progressbar';
import 'react-circular-progressbar/dist/styles.css';
import { FaUserCircle } from 'react-icons/fa';
import { motion } from 'framer-motion';

const Card = ({ children, className }) => (
  <motion.div 
    whileHover={{ scale: 1.05 }}
    whileTap={{ scale: 0.95 }}
    className={`bg-white rounded-xl shadow-lg p-6 ${className}`}
  >
    {children}
  </motion.div>
);

const Button = ({ children, onClick, className }) => (
  <motion.button
    whileHover={{ scale: 1.05 }}
    whileTap={{ scale: 0.95 }}
    onClick={onClick}
    className={`px-6 py-2 bg-gradient-to-r from-blue-500 to-purple-500 text-white rounded-lg shadow-md hover:from-blue-600 hover:to-purple-600 transition-all duration-300 ${className}`}
  >
    {children}
  </motion.button>
);

const courses = [
  { name: 'Mathematics', description: 'Learn problem-solving techniques.' },
  { name: 'Physics', description: 'Explore mechanics, thermodynamics, and more.' },
  { name: 'Chemistry', description: 'Understand organic and inorganic chemistry.' },
  { name: 'Biology', description: 'Dive into genetics, ecology, and cellular biology.' },
  { name: 'Computer Science', description: 'Learn coding, algorithms, and data structures.' },
];

const Home = () => (
  <motion.div 
    initial={{ opacity: 0 }}
    animate={{ opacity: 1 }}
    exit={{ opacity: 0 }}
    className="p-6 bg-gradient-to-r from-blue-50 to-purple-50 min-h-screen"
  >
    <h1 className="text-4xl font-bold mb-6 text-gray-800">Welcome to Our Learning Platform</h1>
    <p className="text-gray-600">Enhance your knowledge with interactive courses.</p>
    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6 mt-6">
      {courses.map((course, index) => (
        <Card key={index}>
          <h3 className="text-xl font-semibold text-gray-800">{course.name}</h3>
          <p className="text-gray-600">{course.description}</p>
        </Card>
      ))}
    </div>
  </motion.div>
);

const LoginPage = ({ onLogin }) => {
  const navigate = useNavigate();
  const handleLogin = () => {
    onLogin('John Doe');
    navigate('/dashboard');
  };

  return (
    <motion.div className="flex justify-center items-center h-screen bg-gradient-to-r from-blue-50 to-purple-50">
      <Card className="w-full max-w-md">
        <h1 className="text-3xl font-bold mb-6 text-gray-800">Login</h1>
        <input type="email" placeholder="Email" className="w-full p-2 mb-4 border rounded-lg" />
        <input type="password" placeholder="Password" className="w-full p-2 mb-4 border rounded-lg" />
        <Button onClick={handleLogin} className="w-full">Login</Button>
      </Card>
    </motion.div>
  );
};

const App = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const handleLogin = (name) => {
    setIsLoggedIn(true);
  };

  return (
    <Router>
      <div className="container mx-auto p-6">
        <nav className="flex justify-between items-center bg-gradient-to-r from-blue-500 to-purple-500 p-4 rounded-lg mb-6 shadow-lg">
          <Link to="/" className="text-white font-semibold text-2xl">Home</Link>
          <div className="flex items-center space-x-6">
            {isLoggedIn ? (
              <>
                <Link to="/dashboard" className="text-white text-lg">Dashboard</Link>
                <Link to="/profile" className="text-white flex items-center space-x-2">
                  <FaUserCircle size={28} className="text-white" />
                </Link>
              </>
            ) : (
              <Link to="/login" className="text-white text-lg">Login</Link>
            )}
          </div>
        </nav>
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/login" element={<LoginPage onLogin={handleLogin} />} />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
