import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Link, Navigate, useNavigate } from 'react-router-dom';
import { CircularProgressbar, buildStyles } from 'react-circular-progressbar';
import 'react-circular-progressbar/dist/styles.css';
import { FaUserCircle, FaBook, FaRocket, FaFlask, FaSeedling, FaLaptopCode } from 'react-icons/fa';
import { motion } from 'framer-motion';

// Animated Components
const Card = ({ children, className }) => (
  <motion.div 
    initial={{ scale: 0.95, opacity: 0 }}
    animate={{ scale: 1, opacity: 1 }}
    whileHover={{ scale: 1.02 }}
    className={`bg-white rounded-xl shadow-2xl transform transition-all duration-300 hover:shadow-xl ${className}`}
  >
    {children}
  </motion.div>
);

const Button = ({ children, onClick, className }) => (
  <motion.button
    whileHover={{ scale: 1.05 }}
    whileTap={{ scale: 0.95 }}
    onClick={onClick}
    className={`px-6 py-3 bg-gradient-to-r from-blue-600 to-purple-600 text-white rounded-xl 
      hover:from-blue-700 hover:to-purple-700 transition-all duration-300 ${className}`}
  >
    {children}
  </motion.button>
);

// Course Icons Mapping
const courseIcons = {
  'Mathematics': <FaBook className="w-12 h-12 text-blue-600" />,
  'Physics': <FaRocket className="w-12 h-12 text-red-500" />,
  'Chemistry': <FaFlask className="w-12 h-12 text-green-500" />,
  'Biology': <FaSeedling className="w-12 h-12 text-emerald-600" />,
  'Computer Science': <FaLaptopCode className="w-12 h-12 text-purple-600" />
};

// Animated Background Elements
const AnimatedBackground = () => (
  <div className="absolute inset-0 overflow-hidden z-0">
    {[...Array(10)].map((_, i) => (
      <motion.div
        key={i}
        className="absolute bg-gradient-to-r from-purple-100 to-blue-100 rounded-full blur-lg"
        initial={{
          scale: 0,
          x: Math.random() * 100 - 50,
          y: Math.random() * 100 - 50,
          opacity: 0
        }}
        animate={{
          scale: 1,
          opacity: 0.3,
          x: Math.random() * 100 - 50,
          y: Math.random() * 100 - 50
        }}
        transition={{
          duration: Math.random() * 5 + 5,
          repeat: Infinity,
          repeatType: 'reverse'
        }}
        style={{
          width: `${Math.random() * 50 + 20}px`,
          height: `${Math.random() * 50 + 20}px`,
        }}
      />
    ))}
  </div>
);

const Dashboard = () => {
  const [studentProgress] = useState([
    { course: 'Mathematics', progress: 80 },
    { course: 'Physics', progress: 70 },
    { course: 'Chemistry', progress: 90 },
  ]);

  return (
    <div className="p-8 bg-gradient-to-br from-blue-50 to-purple-50 min-h-screen relative">
      <AnimatedBackground />
      <h1 className="text-4xl font-bold mb-8 text-gray-800 relative z-10">
        📊 Learning Progress
      </h1>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8 relative z-10">
        {studentProgress.map((item, index) => (
          <Card key={index} className="p-6 bg-white/90 backdrop-blur-sm">
            <div className="flex flex-col items-center">
              <div className="w-32 h-32 mb-4">
                <CircularProgressbar
                  value={item.progress}
                  text={`${item.progress}%`}
                  styles={buildStyles({
                    pathColor: item.progress > 75 ? '#4F46E5' : '#F59E0B',
                    textColor: '#1F2937',
                    trailColor: '#E5E7EB',
                    textSize: '24px'
                  })}
                />
              </div>
              <div className="flex items-center gap-3">
                {courseIcons[item.course]}
                <h3 className="text-2xl font-bold text-gray-800">{item.course}</h3>
              </div>
            </div>
          </Card>
        ))}
      </div>
    </div>
  );
};
const courses = [
  {
    name: 'Mathematics',
    description: 'This course covers fundamental mathematical concepts and problem-solving techniques.',
    icon: <FaBook className="w-12 h-12 text-blue-600" />
  },
  {
    name: 'Physics',
    description: 'Learn the basics of physics, including mechanics, thermodynamics, and electromagnetism.',
    icon: <FaRocket className="w-12 h-12 text-red-500" />
  },
  {
    name: 'Chemistry',
    description: 'A comprehensive course on chemistry, including organic and inorganic chemistry.',
    icon: <FaFlask className="w-12 h-12 text-green-500" />
  },
  {
    name: 'Biology',
    description: 'Explore the world of biology, covering cellular biology, genetics, and ecology.',
    icon: <FaSeedling className="w-12 h-12 text-emerald-600" />
  },
  {
    name: 'Computer Science',
    description: 'Dive into computer science, learning programming, algorithms, and data structures.',
    icon: <FaLaptopCode className="w-12 h-12 text-purple-600" />
  }
];
const Home = () => (
  <div className="p-8 bg-gradient-to-br from-blue-50 to-purple-50 min-h-screen relative">
    <AnimatedBackground />
    <div className="relative z-10">
      <header className="text-center mb-12">
        <motion.h1 
          initial={{ y: -20, opacity: 0 }}
          animate={{ y: 0, opacity: 1 }}
          className="text-5xl font-bold mb-6 text-gray-800"
        >
          🚀 Welcome to FutureLearn
        </motion.h1>
        <p className="text-xl text-gray-600 max-w-3xl mx-auto">
          Master new skills with our interactive courses and hands-on projects. 
          Start your learning journey today!
        </p>
      </header>

      <section className="mb-16">
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
          {courses.map((course, index) => (
            <Card key={index} className="p-6 bg-white/90 backdrop-blur-sm">
              <div className="flex flex-col items-center text-center">
                <div className="mb-4">
                  {courseIcons[course.name]}
                </div>
                <h3 className="text-2xl font-bold text-gray-800 mb-2">{course.name}</h3>
                <p className="text-gray-600">{course.description}</p>
                <Button className="mt-6">Explore Course</Button>
              </div>
            </Card>
          ))}
        </div>
      </section>
    </div>
  </div>
);

// Add these animated components to other pages similarly
// ... (rest of the components with similar animation enhancements)



const Courses = () => {
  const [enrolledCourses, setEnrolledCourses] = useState([]);

  const handleOptCourse = (course) => {
    setEnrolledCourses([...enrolledCourses, course]);
  };

  return (
    <div className="p-6 bg-gradient-to-r from-blue-50 to-purple-50 min-h-screen">
      <h1 className="text-3xl font-bold mb-6 text-gray-800">Courses Available</h1>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {courses.map((course, index) => (
          <Card key={index} className="p-6">
            <CardContent>
              <h2 className="text-xl font-semibold text-gray-800">{course.name}</h2>
              <p className="text-gray-600">{course.description}</p>
              {!enrolledCourses.includes(course.name) ? (
                <Button className="mt-4" onClick={() => handleOptCourse(course.name)}>
                  Opt for Course
                </Button>
              ) : (
                <>
                  <p className="mt-4 text-gray-700">Progress: 50%</p> {/* Replace with actual progress */}
                  <div className="mt-4">
                    <h3 className="text-lg font-semibold text-gray-800">Course Videos</h3>
                    <ul className="list-disc list-inside text-gray-600">
                      <li>Video 1: Introduction</li>
                      <li>Video 2: Advanced Concepts</li>
                    </ul>
                  </div>
                </>
              )}
            </CardContent>
          </Card>
        ))}
      </div>
    </div>
  );
};

const ProfilePage = ({ onLogout }) => {
  const navigate = useNavigate();
  const [profileData, setProfileData] = useState({
    name: 'John Doe',
    email: 'john.doe@example.com',
    courses: [
      { course: 'Mathematics', progress: 80, assignmentSubmitted: true, testGrade: 'A' },
      { course: 'Physics', progress: 70, assignmentSubmitted: false, testGrade: 'B+' },
      { course: 'Chemistry', progress: 90, assignmentSubmitted: true, testGrade: 'A+' },
    ],
  });

  const handleLogout = () => {
    onLogout();
    navigate('/login');
  };

  return (
    <div className="p-6 bg-gradient-to-r from-blue-50 to-purple-50 min-h-screen">
      <h1 className="text-3xl font-bold mb-6 text-gray-800">Profile</h1>
      <div className="mb-6">
        <p className="text-gray-700"><strong>Name:</strong> {profileData.name}</p>
        <p className="text-gray-700"><strong>Email:</strong> {profileData.email}</p>
      </div>
      <div className="mb-6">
        <h2 className="text-2xl font-semibold mb-4 text-gray-800">Courses In Progress:</h2>
        {profileData.courses.map((course, index) => (
          <Card key={index} className="p-6 mb-4">
            <CardContent>
              <h3 className="text-xl font-semibold text-gray-800">{course.course}</h3>
              <div className="w-full bg-gray-200 h-2 mb-4 rounded-full">
                <div
                  className="bg-gradient-to-r from-blue-500 to-purple-500 h-full rounded-full"
                  style={{ width: `${course.progress}%` }}
                ></div>
              </div>
              <p className="text-gray-700"><strong>Progress:</strong> {course.progress}%</p>
              <p className="text-gray-700"><strong>Assignment Submitted:</strong> {course.assignmentSubmitted ? 'Yes' : 'No'}</p>
              <p className="text-gray-700"><strong>Test Grade:</strong> {course.testGrade}</p>
            </CardContent>
          </Card>
        ))}
      </div>
      <Button onClick={handleLogout} className="mt-4">Logout</Button>
    </div>
  );
};

const AssignmentPage = () => {
  const [assignmentStatus, setAssignmentStatus] = useState([
    { course: 'Mathematics', assignmentSubmitted: false },
    { course: 'Physics', assignmentSubmitted: false },
    { course: 'Chemistry', assignmentSubmitted: false },
  ]);

  const handleUploadAssignment = (index) => {
    const newAssignmentStatus = [...assignmentStatus];
    newAssignmentStatus[index].assignmentSubmitted = true;
    setAssignmentStatus(newAssignmentStatus);
  };

  return (
    <div className="p-6 bg-gradient-to-r from-blue-50 to-purple-50 min-h-screen">
      <h1 className="text-3xl font-bold mb-6 text-gray-800">Assignments</h1>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {assignmentStatus.map((item, index) => (
          <Card key={index} className="p-6">
            <CardContent>
              <h2 className="text-xl font-semibold text-gray-800">{item.course}</h2>
              {!item.assignmentSubmitted ? (
                <Button onClick={() => handleUploadAssignment(index)} className="mt-4">Upload Assignment</Button>
              ) : (
                <p className="text-green-600 mt-4">Assignment Submitted</p>
              )}
            </CardContent>
          </Card>
        ))}
      </div>
    </div>
  );
};

const TestPage = () => {
  const [testStatus, setTestStatus] = useState([
    { course: 'Mathematics', testTaken: false, grade: '' },
    { course: 'Physics', testTaken: false, grade: '' },
    { course: 'Chemistry', testTaken: false, grade: '' },
  ]);

  const handleTakeTest = (index) => {
    const newTestStatus = [...testStatus];
    newTestStatus[index].testTaken = true;
    newTestStatus[index].grade = 'A'; // Replace with actual test logic
    setTestStatus(newTestStatus);
  };

  return (
    <div className="p-6 bg-gradient-to-r from-blue-50 to-purple-50 min-h-screen">
      <h1 className="text-3xl font-bold mb-6 text-gray-800">Tests</h1>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {testStatus.map((item, index) => (
          <Card key={index} className="p-6">
            <CardContent>
              <h2 className="text-xl font-semibold text-gray-800">{item.course}</h2>
              {!item.testTaken ? (
                <Button onClick={() => handleTakeTest(index)} className="mt-4">Take Test</Button>
              ) : (
                <p className="text-green-600 mt-4">Grade: {item.grade}</p>
              )}
            </CardContent>
          </Card>
        ))}
      </div>
    </div>
  );
};

const LoginPage = ({ onLogin }) => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const navigate = useNavigate();

  const handleLogin = () => {
    setIsLoggedIn(true);
    onLogin('John Doe');
    navigate('/dashboard');
  };

  if (isLoggedIn) return <Navigate to="/dashboard" replace />;

  return (
    <div className="p-6 flex justify-center items-center h-screen bg-gradient-to-r from-blue-50 to-purple-50">
      <Card className="p-6 w-full max-w-md">
        <CardContent>
          <h1 className="text-3xl font-bold mb-6 text-gray-800">Login</h1>
          <Input type="email" placeholder="Email" className="mb-4" />
          <Input type="password" placeholder="Password" className="mb-4" />
          <Button onClick={handleLogin} className="w-full">Login</Button>
          <p className="mt-4 text-center text-sm text-gray-600">
            Don't have an account? <Link to="/register" className="text-blue-500">Register</Link>
          </p>
        </CardContent>
      </Card>
    </div>
  );
};

const App = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  return (
    <Router>
      <div className="container mx-auto p-6">
        <motion.nav 
          className="flex justify-between items-center bg-white/80 backdrop-blur-md p-4 rounded-2xl mb-8 shadow-lg sticky top-4 z-50"
          initial={{ y: -50, opacity: 0 }}
          animate={{ y: 0, opacity: 1 }}
        >
          <Link to="/" className="text-2xl font-bold bg-gradient-to-r from-blue-600 to-purple-600 bg-clip-text text-transparent">
            FutureLearn
          </Link>
          <div className="flex items-center gap-8">
            {isLoggedIn ? (
              <>
                <NavLink to="/courses" icon="📚" label="Courses" />
                <NavLink to="/assignments" icon="📝" label="Assignments" />
                <NavLink to="/profile" icon={<FaUserCircle className="text-2xl text-gray-700" />} />
              </>
            ) : (
              <NavLink to="/login" icon="🔑" label="Get Started" />
            )}
          </div>
        </motion.nav>

        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/" element={<Home />} />
          <Route path="/dashboard" element={<Dashboard />} />
          <Route path="/login" element={<LoginPage onLogin={handleLogin} />} />
          <Route path="/register" element={<RegisterPage />} />
          <Route path="/profile" element={<ProfilePage onLogout={handleLogout} />} />
          <Route path="/courses" element={<Courses />} />
          <Route path="/test" element={<TestPage />} /> 
          <Route path="/assignments" element={<AssignmentPage />} />
        </Routes>
      </div>
    </Router>
  );
};

const NavLink = ({ to, icon, label }) => (
  <motion.div whileHover={{ scale: 1.05 }}>
    <Link 
      to={to} 
      className="flex items-center gap-2 text-gray-700 hover:text-gray-900 transition-colors"
    >
      <span className="text-xl">{icon}</span>
      {label && <span className="font-medium">{label}</span>}
    </Link>
  </motion.div>
);

export default App;
