import React, { useState } from 'react';
import { useParams } from 'react-router-dom'; // Import useParams
import Card from '../components/ui/card';
import AnimatedBackground from '../components/ui/AnimatedBackground';

const TestPage = () => {
  const { title } = useParams(); // Get the test title from the URL
  const [questions, setQuestions] = useState([
    {
      question: "What is 2 + 2?",
      options: ["2", "4", "6", "8"],
      answer: "4"
    },
    // ... more questions for this test
  ]);

  return (
    <div className="p-8 bg-gradient-to-br from-blue-50 to-purple-50 min-h-screen relative">
      <AnimatedBackground />
      <div className="relative z-10">
        <h1 className="text-4xl font-bold mb-8 text-gray-800">Test: {title}</h1> {/* Display test title */}
        {/* Display questions and options here */}
        {questions.map((questionData, index) => (
            <Card key={index} className="p-6 bg-white/90 backdrop-blur-sm mb-4">
              <div className="flex flex-col">
                <h3 className="text-2xl font-bold text-gray-800 mb-2">
                  {questionData.question}
                </h3>
                {questionData.options.map((option, optionIndex) => (
                  <label key={optionIndex} className="block text-gray-700 mb-2">
                    <input type="radio" name={`question-${index}`} value={option} /> {/* Unique name for each question */}
                    {option}
                  </label>
                ))}
              </div>
            </Card>
          ))}
      </div>
    </div>
  );
};

export default TestPage;
